{
    // United States
    const US = 'EBAY-US';
    // Canada (English)
    const ENCA = 'EBAY-ENCA';
    // UK
    const GB = 'EBAY-GB';
    // Australia
    const AU = 'EBAY-AU';
    // Austria
    const AT = 'EBAY-AT';
    // Belgium (French)
    const FRBE = 'EBAY-FRBE';
    // France
    const FR = 'EBAY-FR';
    // Germany
    const DE = 'EBAY-DE';
    // Motors
    const MOTORS = 'EBAY-MOTOR';
    // Italy
    const IT = 'EBAY-IT';
    // Belgium (Dutch)
    const NLBE = 'EBAY-NLBE';
    // Netherlands
    const NL = 'EBAY-NL';
    // Spain
    const ES = 'EBAY-ES';
    // Switzerland
    const CH = 'EBAY-CH';
    // Hong Kong
    const HK = 'EBAY-HK';
    // India
    const IN = 'EBAY-IN';
    // Ireland
    const IE = 'EBAY-IE';
    // Malaysia
    const MY = 'EBAY-MY';
    // Canada (French)
    const FRCA = 'EBAY-FRCA';
    // Philippones
    const PH = 'EBAY-PH';
    // Poland
    const PL = 'EBAY-PL';
    // Singapore
    const SG = 'EBAY-SG';
}
